mapper.select:
SELECT result.orderNo orderNo , result.customer_Email customerEmail , result.orders_no boardOrdersNo , result.goods_no goodsNo , result.payment_no paymentNo FROM ( SELECT o.orders_no AS `orderNo` , c.customer_email , b.orders_no , o.goods_no , p.payment_no FROM orders o INNER JOIN payment p ON p.payment_no = o.payment_no INNER JOIN address a ON a.address_no = p.address_no INNER JOIN customer c ON c.customer_email = a.customer_email LEFT OUTER JOIN board b ON b.orders_no = o.orders_no WHERE NOT EXISTS ( SELECT 1 FROM board b2 INNER JOIN orders o2 ON b2.orders_no = o2.orders_no WHERE o2.orders_no = o.orders_no ) UNION ALL SELECT o.orders_no , c.customer_email , b.orders_no , o.goods_no , p.payment_no FROM orders o INNER JOIN payment p ON p.payment_no = o.payment_no INNER JOIN address a ON a.address_no = p.address_no INNER JOIN customer c ON c.customer_email = a.customer_email LEFT JOIN board b ON b.orders_no = o.orders_no WHERE EXISTS ( SELECT 1 FROM board b2 INNER JOIN orders o2 ON b2.orders_no = o2.orders_no WHERE o2.orders_no = o.orders_no ) ) result
result.customer_email = #{loginCustomer} AND result.goods_no = #{goodsNo}
ORDER BY `orderNo` ASC

SELECT goods_no goodsNo , goods_title goodsTitle , goods_memo goodsMemo , goods_price goodsPrice , goods_status goodsStatus , update_date updateDate , create_date createDate FROM goods
goods_title LIKE CONCAT( '%', #{searchWord} ,'%' )
LIMIT #{beginRow}, #{rowPerPage}
