mapper.insert:

     	   SELECT LAST_INSERT_ID()


		INSERT INTO payment(
			address_no, payment_price, payment_method, payment_status
		) VALUES (
			#{addressNo}, #{paymentPrice}, #{paymentMethod}, '결제완료'
		)

INSERT INTO orders(
	goods_no, orders_amount, payment_no
) VALUES (
	(SELECT goods_no FROM cart WHERE cart_no=#{cartNo})
	, (SELECT cart_amount FROM cart WHERE cart_no=#{cartNo})
	, #{paymentNo}
)

mapper.select:
SELECT p.payment_no AS paymentNo , p.payment_status AS paymentStatus , p.payment_price AS paymentPrice , p.payment_method AS paymentMethod , p.create_date AS createDate , IFNULL(a.customer_email, "( 회원탈퇴 고객 )") AS customerEmail , CONCAT(COUNT(o.orders_no), '건') AS orderCnt FROM payment p INNER JOIN address a ON p.address_no = a.address_no INNER JOIN orders o ON p.payment_no = o.payment_no
a.customer_email = #{customerEmail}
GROUP BY p.payment_no ORDER BY CASE WHEN p.payment_status = '결제완료' THEN 0 ELSE 1 END, p.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*)	FROM payment p
INNER JOIN 
   (SELECT * FROM address


			customer_email =  #{customerEmail}


	) a 
ON p.address_no = a.address_no

mapper.update:
UPDATE payment SET payment_status = #{paymentStatus}
WHERE payment_no
 = (SELECT paymentNo
 	 FROM (SELECT p.payment_no paymentNo
		   FROM payment p
		   INNER JOIN address a	ON p.address_no = a.address_no

		   		p.payment_no = #{paymentNo}

				 	 AND a.customer_email = #{customerEmail}


		   ) AS target);
